// ===== THEME TOGGLE =====
function ThemeToggle({ dark, setDark }) {
  return (
    <button onClick={() => setDark(!dark)} style={{ position: "fixed", top: 16, right: 16, zIndex: 150, background: dark ? "rgba(255,255,255,0.08)" : "rgba(0,0,0,0.08)", border: "1px solid " + (dark ? "rgba(201,169,110,0.2)" : "rgba(0,0,0,0.15)"), borderRadius: 50, width: 44, height: 44, display: "flex", alignItems: "center", justifyContent: "center", cursor: "pointer", fontSize: 18, transition: "all 0.3s" }}>
      {dark ? "" : ""}
    </button>
  );
}

// ===== NAV =====
function NavBar({ active, setActive, dark }) {
  const tabs = [
    { id: "home", label: "Home", icon: "" },
    { id: "memories", label: "Memories", icon: "" },
    { id: "love", label: "Daily Love", icon: "" },
    { id: "contract", label: "Contract", icon: "" },
    { id: "assets", label: "Assets", icon: "" },
  ];
  return (
    <nav style={{ position: "fixed", bottom: 0, left: 0, right: 0, zIndex: 100, background: dark ? "rgba(10,10,10,0.95)" : "rgba(255,255,255,0.95)", backdropFilter: "blur(12px)", borderTop: "1px solid " + (dark ? "rgba(201,169,110,0.12)" : "rgba(0,0,0,0.1)"), display: "flex" }}>
      {tabs.map(t => (
        <button key={t.id} onClick={() => setActive(t.id)} style={{ flex: 1, padding: "10px 0 8px", background: "none", border: "none", cursor: "pointer", display: "flex", flexDirection: "column", alignItems: "center", gap: 3, color: active === t.id ? "#c9a96e" : (dark ? "#8a8078" : "#999"), transition: "all 0.3s", borderTop: active === t.id ? "2px solid #c9a96e" : "2px solid transparent" }}>
          <span style={{ fontSize: 16 }}>{t.icon}</span>
          <span style={{ fontSize: 9, letterSpacing: "0.08em", fontFamily: "'Cormorant Garamond',serif" }}>{t.label}</span>
        </button>
      ))}
    </nav>
  );
}

// ===== THEME STYLES HELPER =====
function useThemeStyles(dark) {
  return {
    bg: dark ? "#0a0a0a" : "#f8f6f2",
    card: dark ? "rgba(255,255,255,0.02)" : "rgba(0,0,0,0.02)",
    cardBorder: dark ? "rgba(201,169,110,0.1)" : "rgba(0,0,0,0.08)",
    text: dark ? "#f5efe6" : "#1a1a1a",
    textSub: dark ? "#e8e0d4" : "#333",
    textMuted: dark ? "#8a8078" : "#888",
    gold: "#c9a96e",
    rose: "#c4787a",
    surface: dark ? "#111" : "#fff",
    inputBg: dark ? "rgba(255,255,255,0.05)" : "rgba(0,0,0,0.04)",
    inputBorder: dark ? "rgba(201,169,110,0.15)" : "rgba(0,0,0,0.12)",
    overlay: dark ? "rgba(0,0,0,0.85)" : "rgba(0,0,0,0.5)",
  };
}

