// ===== SIGNATURE PAD =====
function SignaturePad({ name, t }) {
  const canvasRef = useRef(null);
  const [drawing, setDrawing] = useState(false);
  const [signed, setSigned] = useState(false);
  const [signatureData, setSignatureData] = useState(null);

  const getPos = (e) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const touch = e.touches ? e.touches[0] : e;
    return {
      x: (touch.clientX - rect.left) * (canvas.width / rect.width),
      y: (touch.clientY - rect.top) * (canvas.height / rect.height)
    };
  };

  const startDraw = (e) => {
    e.preventDefault();
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const pos = getPos(e);
    ctx.beginPath();
    ctx.moveTo(pos.x, pos.y);
    setDrawing(true);
  };

  const draw = (e) => {
    e.preventDefault();
    if (!drawing) return;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const pos = getPos(e);
    ctx.lineTo(pos.x, pos.y);
    ctx.strokeStyle = "#c9a96e";
    ctx.lineWidth = 2;
    ctx.lineCap = "round";
    ctx.lineJoin = "round";
    ctx.stroke();
  };

  const stopDraw = (e) => {
    e.preventDefault();
    setDrawing(false);
  };

  const clearSig = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    setSigned(false);
    setSignatureData(null);
  };

  const confirmSig = () => {
    const canvas = canvasRef.current;
    setSignatureData(canvas.toDataURL());
    setSigned(true);
  };

  return (
    <div style={{ textAlign: "center", minWidth: 200 }}>
      {signed && signatureData ? (
        <div>
          <img src={signatureData} alt={name + " signature"} style={{ width: 200, height: 80, objectFit: "contain" }} />
          <div style={{ display: "flex", gap: 6, justifyContent: "center", marginTop: 6 }}>
            <button onClick={clearSig} style={{ fontSize: "0.7rem", color: t.textMuted, background: "none", border: "1px solid " + t.textMuted + "50", borderRadius: 4, padding: "3px 10px", cursor: "pointer" }}>Re-sign</button>
          </div>
        </div>
      ) : (
        <div>
          <canvas
            ref={canvasRef}
            width={400}
            height={160}
            style={{ width: 200, height: 80, border: "1px solid " + t.cardBorder, borderRadius: 4, background: t.inputBg, cursor: "crosshair", touchAction: "none" }}
            onMouseDown={startDraw}
            onMouseMove={draw}
            onMouseUp={stopDraw}
            onMouseLeave={stopDraw}
            onTouchStart={startDraw}
            onTouchMove={draw}
            onTouchEnd={stopDraw}
          />
          <div style={{ display: "flex", gap: 6, justifyContent: "center", marginTop: 6 }}>
            <button onClick={clearSig} style={{ fontSize: "0.7rem", color: t.textMuted, background: "none", border: "1px solid " + t.textMuted + "50", borderRadius: 4, padding: "3px 10px", cursor: "pointer" }}>Clear</button>
            <button onClick={confirmSig} style={{ fontSize: "0.7rem", color: "#0a0a0a", background: t.gold, border: "none", borderRadius: 4, padding: "3px 10px", cursor: "pointer", fontWeight: 600 }}>Sign</button>
          </div>
        </div>
      )}
      <div style={{ fontFamily: "'Playfair Display',serif", fontSize: "1.1rem", color: t.text, fontWeight: 700, marginTop: 8 }}>{name}</div>
      <div style={{ fontSize: "0.8rem", color: t.textMuted }}>Signature</div>
      <div style={{ fontSize: "0.8rem", color: t.textMuted, marginTop: 8 }}>{signed ? "Signed: " + new Date().toLocaleDateString("en-US", { year: "numeric", month: "long", day: "numeric" }) : "Date: _______________"}</div>
    </div>
  );
}

